import React, { Component, useRef, useEffect } from "react";
import Typical from "react-typical";
import Switch from "react-switch";
import { render } from "react-dom";

const useMountEffect = fun => useEffect(fun, []);

const ScrollDemo = () => {
  const myRef = useRef(null);

  const executeScroll = () => myRef.current.scrollIntoView(); // run this function from an event handler or pass it to useEffect to execute scroll

  useMountEffect(executeScroll); // Scroll on mount

  return (
    <>
      {" "}
      {/* React.Fragment*/}
      <div style={{ height: 600 }} /> {/* just to demonstrate scroll*/}
      <div ref={myRef}>I wanna be seen</div>
      {/* Attach ref object to a dom element */}
      <div style={{ height: 1500 }} /> {/* just to demonstrate scroll*/}
      <button onClick={executeScroll}>Click to scroll </button>
      {/* Scroll on click */}
    </>
  );
};

class Header extends Component {
  titles = [];


  constructor() {
    super();
    this.state = { checked: false };
  }

  render() {
    if (this.props.sharedData) {
      var name = this.props.sharedData.name;
      this.titles = this.props.sharedData.titles.map(x => [ x, 1500 ] ).flat();
      //can make x in [] x.toUpperCase()
    }

    const HeaderTitleTypeAnimation = React.memo( () => {
      return <Typical className="title-styles" steps={this.titles} loop={100} />
    }, (props, prevProp) => true);

    return (
      <header id="home" style={{ height: window.innerHeight - 140, display: 'block' }}>
        <div className="row aligner" style={{height: '100%'}}>
          <div className="col-md-12">
            <div>
              <span className="iconify header-icon" data-icon="la:laptop-code" data-inline="false"></span>
              <br/>
              <h1 className="mb-0">
                <Typical steps={[name]} wrapper="p" />
              </h1>
              <div className="title-container">
                <HeaderTitleTypeAnimation />
              </div>
              <div>
                <h1>
                <span key={"linkedIn"} className="m-4">
                  <a href={"https://www.linkedin.com/in/jaya-sekhon"} target="_blank" rel="noopener noreferrer">
                    <i className={"fab fa-linkedin"}></i>
                  </a>
                </span>
                </h1>
              </div>
            </div>
          </div>
        </div>
      </header>
    );
  }
}

export default Header;
