import React, { Component, createRef } from "react";
import Typical from "react-typical";
import Switch from "react-switch";
import { render } from "react-dom";
import Projects from "./components/Projects";

class Header extends Component {
  titles = [];


  constructor() {
    super();
    this.state = { checked: false };
    this.scrollToProj = createRef();
    this.scrollToExp = createRef();
  }

  render() {
    if (this.props.sharedData) {
      var name = this.props.sharedData.name;
      this.titles = this.props.sharedData.titles.map(x => [ x, 1500 ] ).flat();
      //can make x in [] x.toUpperCase()
    }

    const HeaderTitleTypeAnimation = React.memo( () => {
      return <Typical className="title-styles" steps={this.titles} loop={100} />
    }, (props, prevProp) => true);

    return (
      <header id="home" style={{ height: window.innerHeight - 140, display: 'block' }}>
        <div className="row aligner" style={{height: '100%'}}>
          <div className="col-md-12">
            <div>
              <span className="iconify header-icon" data-icon="la:laptop-code" data-inline="false"></span>
              <br/>
              <h1 className="mb-0">
                <Typical steps={[name]} wrapper="p" />
              </h1>
              <div className="title-container">
                <HeaderTitleTypeAnimation />
              </div>
              <div>
                <h1>
                <span key={"linkedIn"} className="m-4">
                  <a href={"https://www.linkedin.com/in/jaya-sekhon"} target="_blank" rel="noopener noreferrer">
                    <i className={"fab fa-linkedin"}></i>
                  </a>
                  <div className="mx-auto text-center language">
                    <button className="bouncy mx-auto language" onClick={() => this.scrollToProj.current.scrollIntoView({behavior: 'smooth'})}>Projects</button>
                    <button className="bouncy mx-auto language" onClick={() => this.scrollToExp.current.scrollIntoView({behavior: 'smooth'})}>Experience</button>
                  </div>
                </span>

                </h1>

              </div>
            </div>
          </div>
        </div>
        <div ref ={this.scrollToProj} />
        <Projects
          resumeProjects={this.state.resumeData.projects}
          resumeBasicInfo={this.state.resumeData.basic_info} 
        />
      </header>


    );
  }
}
export default Header;
